package loops;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i =1;
		
		while (i<=10) {
			
			System.out.println("Nancy");
			i++;
			//i=i+1
		
			int h = 2;
			while(h<=1) {
				System.out.println("h");
				i++;
				
			}
		}
	
		
    }
	
}